





CREATE VIEW "DessertInfo" AS SELECT DessertMenu.DessertId, DessertMenu.Name, DessertMenu.Category, DessertMenu.Category, DessertMenu.Description, DessertMenu.Price, DessertMenu.ImgSrc, IFNULL(ROUND(AVG(DessertRatings.Rating)), 0) AS AvgRating, COUNT(DessertRatings.DessertFK) AS TotalVotes
FROM DessertMenu
LEFT JOIN DessertRatings ON DessertMenu.DessertId = DessertRatings.DessertFK
GROUP BY DessertMenu.DessertId
ORDER BY DessertMenu.Category ASC;

CREATE VIEW "DrinkInfo" AS SELECT DrinkMenu.DrinkId, DrinkMenu.Name, DrinkMenu.Category, DrinkMenu.Description, DrinkMenu.Price, DrinkMenu.ImgSrc, IFNULL(ROUND(AVG(DrinkRatings.Rating)), 0) AS AvgRating, COUNT(DrinkRatings.DrinkFK) AS TotalVotes
FROM DrinkMenu
LEFT JOIN DrinkRatings ON DrinkMenu.DrinkId = DrinkRatings.DrinkFK
GROUP BY DrinkMenu.DrinkId
ORDER BY DrinkMenu.Category ASC;

CREATE VIEW "FoodInfo" AS SELECT FoodMenu.FoodId, FoodMenu.Name, FoodMenu.Category, FoodMenu.Description, FoodMenu.Price, FoodMenu.ImgSrc, IFNULL(ROUND(AVG(FoodRatings.Rating)), 0) AS AvgRating, COUNT(FoodRatings.FoodFK) AS TotalVotes
FROM FoodMenu
LEFT JOIN FoodRatings ON FoodMenu.FoodId = FoodRatings.FoodFK
GROUP BY FoodMenu.FoodId
ORDER BY FoodMenu.Category ASC;